import React, { useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Rating from '@mui/material/Rating';

//Creating a theme that can hold css styles for mui components.
const theme = createTheme({
  overrides: {
    TextField: {
      root: {
        margin: '10px 0',
        width: '25ch',
      },
    },
  },
});


export default function FeedbackForm() {
  //Defining variables for the inputs and their states
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [rating, setRating] = useState(0);

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  //Logging the results to the console (This should be an API call)
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Name: ', name);
    console.log('Email: ', email);
    console.log('Message: ', message);
    console.log('Rating: ', rating);
  };

  return (
    //Creating a div that will center the component in the middle of the screen.
    <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
    <ThemeProvider theme={theme}>
<br/>
    </ThemeProvider>
        <div>
      <Button variant="outlined" onClick={handleClickOpen}>
        Leave us feedback!
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Leave your feedback</DialogTitle>
        <DialogContent>
          <DialogContentText>
              Thank you for using our website! We appreciate your support and would love to hear your feedback. 
              Your feedback is valuable to us as it will be used to improve website functionality and overall user experience.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
            variant="standard"
            required
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleClose}>Submit</Button>
        </DialogActions>
      </Dialog>
    </div>
    </div>

  );
}
