import React, { useState } from "react";

function FeedbackForm() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [comment, setComment] = useState("");
  const [rating, setRating] = useState(0);
  const [showForm, setShowForm] = useState(false);

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleRatingChange = (event) => {
    setRating(parseInt(event.target.value));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Submit the form data to a server or a database
    console.log({
      name,
      email,
      comment,
      rating,
    });
    // Clear the form inputs
    setName("");
    setEmail("");
    setComment("");
    setRating(0);
    // Hide the form
    setShowForm(false);
  };

  const handleShowForm = () => {
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
  };

  return (
    <div>
      <button onClick={handleShowForm}>Give Feedback</button>
      {showForm && (
        <div>
          <div onClick={handleCloseForm} style={{ position: "fixed", top: 0, left: 0, right: 0, bottom: 0, background: "rgba(0,0,0,0.5)" }}></div>
          <div style={{ position: "fixed", top: "50%", left: "50%", transform: "translate(-50%, -50%)", background: "#fff", padding: "1rem" }}>
            <h2>Feedback Form</h2>
            <form onSubmit={handleSubmit}>
              <label>
                Name:
                <input type="text" value={name} onChange={handleNameChange} />
              </label>
              <br />
              <label>
                Email:
                <input type="email" value={email} onChange={handleEmailChange} />
              </label>
              <br />
              <label>
                Comment:
                <textarea value={comment} onChange={handleCommentChange} />
              </label>
              <br />
              <label>
                Rating:
                <input type="range" min="0" max="10" value={rating} onChange={handleRatingChange} />
                {rating}/10
              </label>
              <br />
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default FeedbackForm;
